import React, { useState } from 'react';
import { 
  View, 
  Text, 
  StyleSheet, 
  TouchableOpacity, 
  ScrollView, 
  SafeAreaView,
  TextInput,
  Modal
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import axios from 'axios';
import API_URL from '../config/api';

export default function ProfileDetailsScreen({ navigation, route }) {
  const [editingField, setEditingField] = useState(null);
  const [tempValue, setTempValue] = useState('');
  const [formData, setFormData] = useState({
    name: 'FABIAN',
    lastName: 'GARCIA',
    email: 'fabian630618@hotmail.com',
    birthDate: 'Fecha de nacimiento',
    identification: 'IdentificaciÃ³n',
    phone: 'Celular'
  });

  const EditModal = ({ field, value, visible, onClose, onSave }) => {
    const [localValue, setLocalValue] = useState(value);

    return (
      <Modal
        visible={visible}
        transparent={true}
        animationType="slide"
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            <Text style={styles.modalTitle}>Editar {field}</Text>
            <TextInput
              style={styles.modalInput}
              value={localValue}
              onChangeText={setLocalValue}
            />
            <View style={styles.modalButtons}>
              <TouchableOpacity style={styles.modalButton} onPress={onClose}>
                <Text style={styles.modalButtonText}>Cancelar</Text>
              </TouchableOpacity>
              <TouchableOpacity 
                style={[styles.modalButton, styles.modalButtonPrimary]}
                onPress={() => onSave(localValue)}
              >
                <Text style={styles.modalButtonTextPrimary}>Guardar</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </Modal>
    );
  };

  const handleFieldSave = async (value) => {
    const newFormData = { ...formData, [editingField]: value };
    setFormData(newFormData);

    // Si se modificÃ³ el nombre o apellido, actualizar la pantalla principal
    if (editingField === 'name' || editingField === 'lastName') {
      const fullName = `${newFormData.name} ${newFormData.lastName}`;
      // Actualizar el nombre en la pantalla principal
      route.params?.onUpdateName?.(fullName);
    }

    setEditingField(null);
  };

  const handleSaveAll = async () => {
    try {
      const response = await axios.put(`${API_URL}/users/profile`, formData);
      if (response.status === 200) {
        // Actualizar el nombre en la pantalla principal
        const fullName = `${formData.name} ${formData.lastName}`;
        route.params?.onUpdateName?.(fullName);
        navigation.goBack();
      }
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  const FormField = ({ label, field, value, editable = true, validated = false }) => (
    <View style={styles.fieldContainer}>
      <Text style={styles.fieldLabel}>{label}</Text>
      <View style={styles.inputContainer}>
        <Text style={styles.fieldValue}>{value}</Text>
        {editable && (
          <TouchableOpacity onPress={() => setEditingField(field)}>
            <Ionicons name="pencil" size={20} color="#4A90E2" />
          </TouchableOpacity>
        )}
      </View>
      {validated && (
        <View style={styles.validationContainer}>
          <Ionicons name="checkmark-circle" size={16} color="#4CAF50" />
          <Text style={styles.validationText}>Correo electrÃ³nico validado</Text>
        </View>
      )}
    </View>
  );

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="#4A90E2" />
        </TouchableOpacity>
        <Text style={styles.headerTitle}>Datos de perfil</Text>
      </View>

      <ScrollView style={styles.content}>
        {/* Form Fields */}
        <FormField label="*Nombre" field="name" value={formData.name} />
        <FormField label="*Apellido" field="lastName" value={formData.lastName} />
        <FormField 
          label="*Correo electrÃ³nico" 
          field="email"
          value={formData.email} 
          validated={true}
        />
        <FormField label="Fecha de nacimiento" field="birthDate" value={formData.birthDate} />
        <FormField label="IdentificaciÃ³n" field="identification" value={formData.identification} />
        <FormField label="Celular" field="phone" value={formData.phone} />

        {/* Phone Verification Warning */}
        <View style={styles.warningContainer}>
          <Ionicons name="warning" size={20} color="#FFA000" />
          <Text style={styles.warningText}>
            Es necesario verificar el nÃºmero de telÃ©fono
          </Text>
        </View>

        {/* Emergency Contact */}
        <TouchableOpacity style={styles.emergencyContact}>
          <Text style={styles.emergencyContactText}>Contacto de emergencia</Text>
          <Ionicons name="chevron-down" size={24} color="#666" />
        </TouchableOpacity>

        {/* Delete Account */}
        <TouchableOpacity style={styles.deleteAccount}>
          <Ionicons name="trash-outline" size={24} color="#666" />
          <Text style={styles.deleteAccountText}>Eliminar cuenta</Text>
          <Ionicons name="chevron-forward" size={24} color="#666" />
        </TouchableOpacity>

        {/* Save Button */}
        <TouchableOpacity style={styles.saveButton} onPress={handleSaveAll}>
          <Text style={styles.saveButtonText}>Guardar</Text>
        </TouchableOpacity>
      </ScrollView>

      <EditModal
        field={editingField}
        value={formData[editingField] || ''}
        visible={!!editingField}
        onClose={() => setEditingField(null)}
        onSave={handleFieldSave}
      />
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F5F5F5',
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    padding: 16,
    backgroundColor: '#FFFFFF',
    borderBottomWidth: 1,
    borderBottomColor: '#E0E0E0',
  },
  headerTitle: {
    fontSize: 20,
    fontWeight: '500',
    marginLeft: 16,
  },
  content: {
    flex: 1,
    padding: 16,
  },
  fieldContainer: {
    backgroundColor: '#FFFFFF',
    borderRadius: 12,
    padding: 16,
    marginBottom: 16,
  },
  fieldLabel: {
    fontSize: 14,
