import React from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
  Image,
  Dimensions,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';

const { width } = Dimensions.get('window');

export default function OnboardingScreen({ navigation }) {
  return (
    <SafeAreaView style={styles.container}>
      {/* Close Button */}
      <TouchableOpacity 
        style={styles.closeButton}
        onPress={() => navigation.goBack()}
      >
        <Ionicons name="close" size={24} color="#666" />
      </TouchableOpacity>

      <View style={styles.content}>
        {/* Illustration */}
        <Image
          source={require('../assets/onboarding-illustration.png')}
          style={styles.illustration}
          resizeMode="contain"
        />

        {/* Text Content */}
        <Text style={styles.title}>
          Sum치 a m치s personas{'\n'}a la gesti칩n de tu{'\n'}hogar
        </Text>

        <Text style={styles.description}>
          Ahora te damos la posibilidad de agregar personas a tu unidad y hacer una gesti칩n del hogar compartida.
        </Text>

        {/* Pagination Dots */}
        <View style={styles.paginationContainer}>
          <View style={[styles.dot, styles.activeDot]} />
          <View style={styles.dot} />
          <View style={styles.dot} />
        </View>
      </View>

      {/* Next Button */}
      <TouchableOpacity 
        style={styles.nextButton}
        onPress={() => navigation.navigate('OnboardingStep2')}
      >
        <Text style={styles.nextButtonText}>Siguiente</Text>
      </TouchableOpacity>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  // ... todos los estilos que proporcionaste
});
